import numpy as np

a = np.arange(-10, 10)
print(">>> ", a)
print(">>> ", np.abs(a))

print("------------------------------------------------------------------------------------------")
a = np.arange(10)
print(">>> ", a)
print(">>> ", np.sqrt(a))

print("------------------------------------------------------------------------------------------")
a = np.random.randint(0, 100, 15)
print(">>> ", a)
print(">>> ", np.max(a))
