import numpy as np

a = np.arange(0, 10).reshape(2, 5)
b = np.arange(10, 20).reshape(2, 5)
print(">>> ", a.shape, b.shape)
print(">>> ", a, b)
print("------------------------------------------------------------------------------------------")
print(">>> ", a + b)
print(">>> ", a - b)
print(">>> ", a * b)
print(">>> ", a / b)
print("------------------------------------------------------------------------------------------")
print(">>> ", a & b)
print(">>> ", a | b)
