def create_wget_script(folder, output_location):
    with open(folder / "download_all.sh", "w") as f:
        f.write("#!/bin/bash\n")
        for o in folder.iterdir():
            if not str(o).endswith("sh"):
                f.write(f"wget {output_location}/{o}\n")